import styled from "styled-components";

export const Wrapper = styled.div`
  display: flex;
  width: 100%;
  right:20%;
  padding: 20px;
  background:#000;

  input {
    border: 2px solid #ccc;
    width: 50%;
    height: 50px;
    padding: 10px;
    border-radius: 5px;
    font-weight: 300;
  }

  button {
    background-color: #225ed8;
    padding: 8px 16px;
    margin: 0 16px;
    

    &:hover {
      background-color: #2c5282;
      box-shadow: 3px 2px 10px rgba(0, 0, 0, 0.2);
    }

    span {
      font-weight: bold;
      color: #ffff;
    }
  }
`;
