import styled from "styled-components";

export const Wrapper = styled.div`
  
display: flex;
  width: 95vw;
  padding: 20px;
  background:#FFFDE4;
  justify-content:flex-end;

  input {
    border: 2px solid #ccc;
    font-weight:bold;
    font-size:20px;
    width: 50vw;
    height: 10vh;
    border-radius:8px;
  }

  button {
    background:#000C40;
    padding: 10px 25px;
    font-size:10px;
    margin: 0 16px;
    border-radius:8px;
    cursor:pointer;

    &:hover {
      
      box-shadow: 3px 2px 10px rgba(0, 0, 0, 0.2);
    }

    span {
      font-weight: bold;
      color: #F0F2F0;
    }
  }
`;
