import styled from "styled-components";

export const Wrapper = styled.div`
  padding: 8px;
  border-radius: 8px;
  border: 1px solid #ccc;
  margin: 16px 16px;
  width: 350px;
  height: 100px;
  align-content: center;
`;

export const WrapperTitle = styled.h2`
  font-size: 18px;
  font-weight: bold;
  padding:20px 0;
`;

export const WrapperLink = styled.a`
  font-size: 16px;
  font-weight: 300;
  margin: 8px 0;
  color: #000;
  text-decoration:none;
  cursor:pointer;
`;
