import { createGlobalStyle } from "styled-components";

export const ResetCSS = createGlobalStyle`
:root{
  --background:#FFFDE4;
}  


*{
    margin:0;
    padding:0;
    font-family:'Roboto', sans-serif;
  }

  body{
    height:100vh;
    background:var(--background);
  }
`;
